// JavaScript Document

var COVERDIV = {
    //_COVER_DIV_LIREN_1: null, _COVER_DIV_LIREN_2: null,
    Show: function (Url, Title, Width, Height) {
        Title += '  -  【按ESC关闭】'
        var W = parseInt(Width); var H = parseInt(Height);
        var MarginL = parseInt(Width + 20) / 2;
        var str1 = "this.style.background='#ffffff'";
        var str2 = "this.style.background='#f1f1f1'";
        document.getElementById('__floatDiv').innerHTML = document.getElementById('__floatDiv').innerHTML
        + '<div id="_COVER_DIV_LIREN_1" style="left:0;top:0;width:100%;height:100%;position:fixed;z-index:100000;opacity:0.3; filter:alpha(opacity=30);">'
        + '<iframe src="about:blank" style="filter:progid:DXImageTransform.Microsoft.Alpha(opacity:0);position:absolute;left:0;top:0;width:100%;height:100%;z-index:-1;" frameborder="0"></iframe>'
        + '<div style="position:absolute;left:0;top:0;width:100%;height:100%;z-index:-1;"></div>'
        + '</div>'
        // 上面是遮盖DIV，下面是浮动DIV（内含iframe元素）
        + '<div id="_COVER_DIV_LIREN_2" style="width:' + parseInt(Width + 20) + 'px;top:100px;left:50%;margin-left:-' + MarginL + 'px;z-index:100001;position:fixed;background:#fff;overflow:hidden;_zoom:1;  border: 1px solid #CCC;">'
        + '<div style="font-family:宋体;font-size:14px;height:34px;line-height:35px;background:#f1f1f1;border-bottom:1px solid #aaa;overflow:hidden;">'
        + '<span style="width:80%;float:left;text-indent:10px;overflow:hidden;" >' + Title + '</span>'
        + '<span style="width:60px;float:right;text-align:center;"><a style="cursor:pointer;" onmouseover="' + str1 + '" onmouseout="' + str2 + '" onclick="COVERDIV.Hide()" >[关闭]</a></span>'
        + '</div>'
        + '<div style="width:' + W + 'px;height:' + H + 'px;padding:10px;">'
        + '<iframe src="' + Url + '" frameborder="0" style="width:100%;height:100%;"></iframe>'
        + '</div>'
        + '</div>';
        //_COVER_DIV_LIREN_1 = document.getElementById('_COVER_DIV_LIREN_1');
        //_COVER_DIV_LIREN_2 = document.getElementById('_COVER_DIV_LIREN_2');
    },
    Hide: function () {
        document.getElementById('__floatDiv').innerHTML = '';
        //parent.document.getElementById('__floatDiv').innerHTML = '';
        //top.document.removeChild('_COVER_DIV_LIREN_1');
        //top.document.removeChild('_COVER_DIV_LIREN_2');
    },
    Hide1: function () {
        //document.getElementById('__floatDiv').innerHTML = '';
        parent.document.getElementById('__floatDiv').innerHTML = '';
        //top.document.removeChild('_COVER_DIV_LIREN_1');
        //top.document.removeChild('_COVER_DIV_LIREN_2');
    }
}




